import React, { useRef } from 'react';
import Hero from './components/Hero';
import Projects from './components/Projects'; // Your project content component
import Contact from './components/Contact';  // Your contact content component
import { Parallax, ParallaxLayer } from '@react-spring/parallax';

function App() {
  const contentRef = useRef(null);

  const scrollToContent = () => {
    contentRef.current.scrollIntoView({ behavior: 'smooth' });
  };

  return (
    <div className="App">
        <Parallax pages={3}>
          <ParallaxLayer speed={4}>
            <Hero scrollToContent={scrollToContent} />
          </ParallaxLayer>
          <ParallaxLayer offset={0}>
            <div ref={contentRef}>
              <Projects />
              <Contact />
            </div>
          </ParallaxLayer>
        </Parallax>
    </div>
  );
}

export default App;
