import React from 'react';
import { Parallax, ParallaxLayer } from '@react-spring/parallax';
import './Hero.css';

const Hero = ({ scrollToContent }) => {
  return (
    <div>
      <Parallax pages={2}>
        {/* Background Image Layer */}
        <ParallaxLayer
          offset={0}
          speed={1}
          style={{
            backgroundImage: `url(${require('../assets/simarMain1.JPG')})`,
            backgroundSize: 'cover',
          }}
        >
          {/* Empty layer for background image */}
        </ParallaxLayer>

        {/* Sticky Text Layer */}
        <ParallaxLayer sticky={{ start: 0.1, end: 0.3 }}>
          <div className="overlay">
            <h1 className="name">Hello, I am Simar!</h1>
            <h3 className="description">B.S. Computer Science @ UC Irvine</h3>
            <button className="discoverMoreButton" onClick={scrollToContent}>
              Discover More
            </button>
          </div>
        </ParallaxLayer>
      </Parallax>
    </div>
  );
};

export default Hero;
