import React from 'react';
import './Hero.css';

const Hero = ({ scrollToContent }) => {
    const heroRef = useRef(null);

    useEffect(() => {
        const controller = new ScrollMagic.Controller();

        // Create ScrollMagic scene for shrinking effect
        const scene = new ScrollMagic.Scene({
        triggerElement: heroRef.current,
        duration: "100%", // The amount of scrolling it will shrink over
        triggerHook: 0, // Trigger at the top of the viewport
        })
        .setTween(heroRef.current, {
        scale: 0.8, // Shrinks the image to 80% of its original size
        transformOrigin: "center top", // Keeps the shrink centered at the top
        })
        .addTo(controller);

        return () => {
        controller.destroy();
        };
    }, []);

  return (
    <section className="hero">
      <img src={require('../assets/simarMain1.JPG')} alt="Background" className="hero-image" />
      <div className="overlay">
        <h1 className="name">Hello, I am Simar!</h1>
        <h3 className="description">B.S. Computer Science @ UC Irvine</h3>
        <button className="discoverMoreButton" onClick={scrollToContent}>Discover More</button>
      </div>
    </section>
  );
};

export default Hero;
