import React from 'react';
import './Hero.css';
import { Parallax, ParallaxLayer } from '@react-spring/parallax';

const Hero = ({ scrollToContent }) => {
  return (
    <div>
        <Parallax>
            <section className="hero">
                {/* <ParallaxLayer> */}
                    <img src={require('../assets/simarMain1.JPG')} alt="Background" className="hero-image"/>
                {/* </ParallaxLayer> */}
                <div className="overlay">
                    <ParallaxLayer speed={1}>
                        <h1 className="name">Hello, I am Simar!</h1>
                        <h3 className="description">B.S. Computer Science @ UC Irvine</h3>
                        <button className="discoverMoreButton" onClick={scrollToContent}>Discover More</button>
                    </ParallaxLayer>
                </div>
            </section>
        </Parallax>
    </div>
  );
};

export default Hero;
