import React from 'react';
import './Experience.css';
import { useInView } from 'react-intersection-observer';

const Experience = () => {

    const { ref: ref1, inView: inView1 } = useInView({
        threshold: 0.1, // Trigger when 10% of the element is in view
        triggerOnce: true, // Run animation only once when the element is in view
    });

    const { ref: ref2, inView: inView2 } = useInView({
        threshold: 0.1,
        triggerOnce: true,
    });

    const { ref: ref3, inView: inView3 } = useInView({
        threshold: 0.1,
        triggerOnce: true,
    });

    return (
        <section className="experience">
            <h2 className="experience-heading">My Experience</h2>
            <div className="timeline">

                <div ref={ref1} className={`timeline-item ${inView1 ? 'fade-in-visible' : 'fade-in'}`}>
                    <div className="timeline-icon"></div>
                    <div className="timeline-content">
                        <h3>Software Engineer Intern</h3>
                        <p >Major League Baseball [MLB] | El Segundo | June 2024 - September 2024</p>
                        <p><strong>Responsibilities:</strong> Brief description of responsibilities and tasks.</p>
                    </div>
                </div>

                <div ref={ref2} className={`timeline-item ${inView2 ? 'fade-in-visible' : 'fade-in'}`}>
                    <div className="timeline-icon"></div>
                    <div className="timeline-content">
                        <h3>Experience #2</h3>
                        <p><em>Role | Year</em></p>
                        <p><strong>Company:</strong> Company Name</p>
                        <p><strong>Responsibilities:</strong> Brief description of responsibilities and tasks.</p>
                    </div>
                </div>

                <div ref={ref3} className={`timeline-item ${inView3 ? 'fade-in-visible' : 'fade-in'}`}>
                    <div className="timeline-icon"></div>
                    <div className="timeline-content">
                        <h3>Experience #3</h3>
                        <p><em>Role | Year</em></p>
                        <p><strong>Company:</strong> Company Name</p>
                        <p><strong>Responsibilities:</strong> Brief description of responsibilities and tasks.</p>
                    </div>
                </div>
            </div>
        </section>
    );
};

export default Experience;
