// import React from 'react';
import './Hero.css';
import React, { useEffect } from 'react';

const Hero = ({ scrollToContent }) => {

  useEffect(() => {
    const handleScroll = () => {
      const scrollPosition = window.scrollY;
      const threshold = 300;
      const background1 = document.querySelector('.background1');
      const background2 = document.querySelector('.background2');
      const background3 = document.querySelector('.background3');
      const name = document.querySelector('.name');

      if (scrollPosition < threshold) {
        // Apply different scroll speeds for each background before the threshold
        background1.style.transform = `translateY(${scrollPosition * 0.1}px)`; // Slowest
        background2.style.transform = `translateY(${scrollPosition * 0.3}px)`; // Medium speed
        background3.style.transform = `translateY(${scrollPosition * 0.5}px)`; // Fastest
        name.style.transform = `translate(-50%, ${scrollPosition * 0.4}px)`; // Name slower than background3
      } else {
        // After the threshold, make everything scroll together
        const lockedScroll = threshold * 0.1; // Calculating the locked position based on threshold
        background1.style.transform = `translateY(${lockedScroll}px)`;
        background2.style.transform = `translateY(${scrollPosition - (threshold - lockedScroll)}px)`;
        background3.style.transform = `translateY(${scrollPosition - (threshold - lockedScroll)}px)`;
        name.style.transform = `translate(-50%, ${scrollPosition - (threshold - lockedScroll)}px)`;
      }
    };

    // Attach the scroll listener
    window.addEventListener('scroll', handleScroll);

    // Cleanup the listener when the component unmounts
    return () => {
      window.removeEventListener('scroll', handleScroll);
    };
  }, []);
  return (
    <div>
            {/* <section className="hero">
                    <img src={require('../assets/simarMain1.JPG')} alt="Background" className="hero-image"/>
                <div className="overlay">
                        <h1 className="name">Hello, I am Simar!</h1>
                        <h3 className="description">B.S. Computer Science @ UC Irvine</h3>
                        <button className="discoverMoreButton" onClick={scrollToContent}>Discover More</button>
                </div>
            </section> */}
            <div className='background-container'>
                <img src={require('../assets/1.png')} alt="Background" className="background1"/>
                <h1 className="name"> Simar </h1>
                <img src={require('../assets/2.png')} alt="Background" className="background2"/>
                <img src={require('../assets/3.png')} alt="Background" className="background3"/>
            </div>
    </div>
  );
};

export default Hero;
